import { useEffect, useMemo, useState } from "react";
import {
  fetchMeals,
  createMeal,
  updateMeal,
  deleteMeal,
  clearWeek
} from "./api";

const days = [
  "Monday",
  "Tuesday",
  "Wednesday",
  "Thursday",
  "Friday",
  "Saturday",
  "Sunday"
];

const emptyMeal = {
  day: "Monday",
  breakfast: "",
  lunch: "",
  dinner: "",
  snacks: "",
  notes: ""
};

function App() {
  const [meals, setMeals] = useState([]);
  const [form, setForm] = useState(emptyMeal);
  const [editingDay, setEditingDay] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const orderedMeals = useMemo(
    () => meals.slice().sort((a, b) => days.indexOf(a.day) - days.indexOf(b.day)),
    [meals]
  );

  useEffect(() => {
    load();
  }, []);

  async function load() {
    setLoading(true);
    try {
      setMeals(await fetchMeals());
      setError("");
    } catch (e) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  }

  function handleChange(e) {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setLoading(true);
    try {
      if (!editingDay) {
        await createMeal(form);
      } else {
        await updateMeal(editingDay, form);
      }
      await load();
      setForm(emptyMeal);
      setEditingDay(null);
    } catch (e) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  }

  function startEdit(meal) {
    setForm(meal);
    setEditingDay(meal.day);
  }

  async function handleDelete(day) {
    if (!window.confirm(`Delete meal for ${day}?`)) return;
    setLoading(true);
    try {
      await deleteMeal(day);
      await load();
    } catch (e) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  }

  async function handleClearWeek() {
    if (!window.confirm("Clear the entire week?")) return;
    setLoading(true);
    try {
      await clearWeek();
      await load();
    } catch (e) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div style={{ maxWidth: 900, margin: "0 auto", padding: "1rem" }}>
      <h1>Weekly Meal Planner</h1>
      {error && <div style={{ color: "red" }}>{error}</div>}
      <form
        onSubmit={handleSubmit}
        style={{
          border: "1px solid #ccc",
          padding: "1rem",
          borderRadius: 8,
          marginBottom: "1rem"
        }}
      >
        <div style={{ display: "flex", gap: "0.5rem", flexWrap: "wrap" }}>
          <label>
            Day
            <select
              name="day"
              value={form.day}
              onChange={handleChange}
              disabled={!!editingDay}
            >
              {days.map((d) => (
                <option key={d} value={d}>
                  {d}
                </option>
              ))}
            </select>
          </label>
          <label>
            Breakfast
            <input name="breakfast" value={form.breakfast} onChange={handleChange} required />
          </label>
          <label>
            Lunch
            <input name="lunch" value={form.lunch} onChange={handleChange} required />
          </label>
          <label>
            Dinner
            <input name="dinner" value={form.dinner} onChange={handleChange} required />
          </label>
          <label>
            Snacks
            <input name="snacks" value={form.snacks} onChange={handleChange} required />
          </label>
          <label>
            Notes
            <input name="notes" value={form.notes} onChange={handleChange} />
          </label>
        </div>
        <button type="submit" disabled={loading} style={{ marginTop: "0.75rem" }}>
          {editingDay ? "Update Meal" : "Add Meal"}
        </button>
        {editingDay && (
          <button
            type="button"
            onClick={() => {
              setEditingDay(null);
              setForm(emptyMeal);
            }}
            style={{ marginLeft: "0.5rem" }}
          >
            Cancel Edit
          </button>
        )}
      </form>

      <div
        style={{
          marginBottom: "0.5rem",
          display: "flex",
          justifyContent: "space-between"
        }}
      >
        <h2>Week</h2>
        <button onClick={handleClearWeek} disabled={loading || meals.length === 0}>
          Clear Week
        </button>
      </div>

      {loading && <div>Loadingâ€¦</div>}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(240px, 1fr))",
          gap: "0.75rem"
        }}
      >
        {orderedMeals.map((meal) => (
          <div
            key={meal.day}
            style={{
              border: "1px solid #ddd",
              borderRadius: 8,
              padding: "0.75rem"
            }}
          >
            <h3>{meal.day}</h3>
            <div>
              <strong>Breakfast:</strong> {meal.breakfast}
            </div>
            <div>
              <strong>Lunch:</strong> {meal.lunch}
            </div>
            <div>
              <strong>Dinner:</strong> {meal.dinner}
            </div>
            <div>
              <strong>Snacks:</strong> {meal.snacks}
            </div>
            {meal.notes && (
              <div>
                <strong>Notes:</strong> {meal.notes}
              </div>
            )}
            <div style={{ marginTop: "0.5rem", display: "flex", gap: "0.5rem" }}>
              <button onClick={() => startEdit(meal)}>Edit</button>
              <button onClick={() => handleDelete(meal.day)}>Delete</button>
            </div>
          </div>
        ))}
        {orderedMeals.length === 0 && !loading && <div>No meals yet</div>}
      </div>
    </div>
  );
}

export default App;
